import React, { useState, useEffect } from "react";
import CountryList from "../components/CountryList";
import CountryDetail from "../components/CountryDetail";
import CountrySelector from "../components/CountrySelect";


const CountriesContainer = () => {
    const [countries, setCountries] = useState([])
    const [selectedCountry, setSelectedCountry] = useState(null)

    useEffect(() => {
        getCountries();
    }, [])

    const getCountries = function(){
        fetch('https://restcountries.com/v3.1/all')
        .then(res => res.json())
        .then(data => setCountries(data))
    }

    const onCountryClick = function(country){
        setSelectedCountry(country);
    }

    return (
        <div>
            whatevs
            <CountrySelector countries={countries} onCountryClick={onCountryClick}/>

            {selectedCountry ? <CountryDetail country={selectedCountry}/> : <CountryList countries={countries} onCountryClick={onCountryClick}/>}


        </div>
    )
//fave countries list
//function: add to list and pass down

}



export default CountriesContainer;
