import React from "react";

const CountryDetail = ({country, addToFaveCountries, countries}) => {

    const handleClick = function({event}){
        const chosenCountry = countries[event.target.value]
        addToFaveCountries({chosenCountry})
    }

    return( 
    <>
    <p>Area: {country.area}km<sup>2</sup></p>
    <p id='flag'>{country.flag}</p>
    <button onClick={handleClick} >Add to faves</button>


    </>
    )
};


export default CountryDetail
